import './Home.css';
import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import queryString from 'query-string';
import socketIOClient from 'socket.io-client';

let socket;

const Home = ({ location }) => {
   const [messages, setMessages] = useState([]);
   const [message, setMessage] = useState('');
   const [users, setUsers] = useState([]);
   const { name } = queryString.parse(location.search);

   useEffect(() => {
      socket = socketIOClient('localhost:5000');

      socket.emit('join', name);

      socket.on('init', (usrList) => {
         setUsers((users) => usrList);
         setMessages((oldMessages) => [
            ...oldMessages,
            { class: 'admin', content: `Welcome to the server ${name}` },
         ]);
      });

      socket.on('usrJoin', (users, name) => {
         setUsers((oldUsers) => users);
         setMessages((oldMessages) => [
            ...oldMessages,
            { class: 'admin', content: `${name} has joined` },
         ]);
      });

      socket.on('usrLeave', (users, name) => {
         setUsers((oldUsers) => users);
         setMessages((oldMessages) => [
            ...oldMessages,
            { class: 'admin', content: `${name} has left` },
         ]);
      });
   }, []);

   return (
      <div className="home">
         <div className="header">
            <Link to={'/'}>
               <button>Logout</button>
            </Link>
            <div className="title">Soulfly</div>
         </div>

         <div className="body">
            <div className="users">
               {users.map((user, index) => (
                  <div className="user" key={index}>
                     {user}
                  </div>
               ))}
            </div>

            <div className="chats">
               <div className="log">

                  {/* <div className="own-messages"> */}
                  {/*    <b>salman: </b> */}
                  {/*    Hey there */}
                  {/* </div> */}

                  {/* <div className="seen-status">seen by saadman</div> */}

                  {/* <div className="others-messages"> */}
                  {/*    <b>saadman: </b> */}
                  {/*    Hi brother */}
                  {/* </div> */}

                  {/* <div className="typing-status">saadman is typing</div> */}

                  {messages.map((message, index) => (
                     <div className={message.class} key={index}>
                        {message.content}
                     </div>
                  ))}

               </div>

               <input
                  onChange={(e) => setMessage(e.target.value)}
                  // onInput={(e) => showTyping()}
                  // onFocus={(e) => showSeen()}
                  onKeyDown={(e) => {
                     if (e.key === 'Enter') {
                        console.log('pressed enter')
                        // e.preventDefault();
                        // pushSendMessage(message);
                        // setMessage('');
                     }
                  }}
               />


            </div>
         </div>
      </div>
   );
};

export default Home;
