import './Home.css';
import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import queryString from 'query-string';
import socketIOClient from 'socket.io-client';

let socket;

const Home = ({ location }) => {
   const [users, setUsers] = useState([]);
   const { name } = queryString.parse(location.search);

   useEffect(() => {

      socket = socketIOClient('localhost:5000');
      socket.emit('join', uName);

      socket.on('init', (usrList) => {
         setUsers(users => [...users, usrList]);
      });

      socket.on('usrJoin', (usr) => {
         setUsers(users => [...users, usr]);
      });

      return () => {
         socket.disconnect();
         // socket.emit('usrLeave', name);
         // socket.off();
      };
   },[]);

   return (
      <div className="home">
         <div className="header">
            <Link to={'/'}>
               <button>Logout</button>
            </Link>
            <div className="title">Soulfly</div>
         </div>

         <div className="body">
            <div className="users">
               {/* <div className="user">gulzer</div> */}
               {/* <div className="user">saadman</div> */}
               {/* <div className="user">salman</div> */}

               {users.map((user, index) => (
                  ( index > 0 ) &&
                  <div className="user" key={index}>
                     {user}
                  </div>
               ))}
            </div>

            <div className="chats">
               <div className="log">

                  {/* <div className="admin">Welcome to the server {name}</div> */}

                  <div className="own-messages">
                     <b>salman: </b>
                     Hey there
                  </div>

                  <div className="seen-status">seen by saadman</div>

                  <div className="others-messages">
                     <b>saadman: </b>
                     Hi brother
                  </div>

                  <div className="typing-status">saadman is typing</div>

                  <div className="admin">Saadman has left</div>
               </div>
               <input />
            </div>
         </div>
      </div>
   );
};

export default Home;
