import './Home.css';
import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import queryString from 'query-string';
import socketIOClient from 'socket.io-client';

let socket;

const Home = ({ location }) => {
   const [uName, setName] = useState('');
   const [users, setUsers] = useState(['1st', '2nd']);
   // const users2 = [ 'salman', 'saadman' ];

   useEffect(() => {
      const { name } = queryString.parse(location.search);
      setName(name);

      socket = socketIOClient('localhost:5000');
      socket.emit('join', name);

      socket.on('init', (usrList) => {
         setUsers([...users, usrList]);
         console.log(users)
      });

      socket.on('usrJoin', (usr) => {
         console.log(`${usr} joined`)
         setUsers([...users, usr]);
         console.log(users)
      });

      return () => {
         socket.disconnect()
         // socket.emit('usrLeave', name);
         // socket.off();
      };

   }, []);

   return (
      <div className="home">
         <div className="header">
            <Link to={'/'}>
               <button>Logout</button>
            </Link>
            <div className="title">Soulfly</div>
         </div>

         <div className="body">
            <div className="users">

               {/* <div className="user">gulzer</div> */}
               {/* <div className="user">saadman</div> */}
               {/* <div className="user">salman</div> */}

               {
                   users.map((user, index) => (
                      <div className="user" key={index}>{user}</div>
                   ))
               }

            </div>

            <div className="chats">
               <div className="log">
                  <div className="admin">Welcome to the server {uName}</div>

                  <div className="own-messages">
                     <b>salman: </b>
                     Hey there
                  </div>

                  <div className="seen-status">seen by saadman</div>

                  <div className="others-messages">
                     <b>saadman: </b>
                     Hi brother
                  </div>

                  <div className="typing-status">saadman is typing</div>

                  <div className="admin">Saadman has left</div>
               </div>
               <input />
            </div>
         </div>
      </div>
   );
};

export default Home;
